(function () { //PLUG IN THAT CONROLS THE SKILLS SECTION BARS THAT GROW TO A PERCENTAGE
    function finish() {
        var opt = this.options,
            elem = document.querySelectorAll(opt.finishThis),
            el,
            i,
            inner,
            percent,
            finishHandle = function () {
                for (i = 0; i < elem.length; i += 1) {
                    if (utilities.checkVisible(elem[i]) === true) {
                        el = elem[i];
                        percent = el.getAttribute('data-finish-percent');
                        inner = el.children[0];
                        inner.classList.add('finishline__initial');
                        inner.style.backgroundColor = opt.barcolor;
                        el.style.backgroundColor = opt.contrast;
                        inner.style.maxWidth = percent + "%";
                        el.removeEventListener('scroll', finishHandle);
                    }
                }
            };
        window.addEventListener('scroll', utilities.throttle(finishHandle, 200));
    }
    this.FinishLine = function () {
        var defaults = { //Default Options
            finishThis: ".js__finishme",
            barcolor: "#22264b",
            contrast: "#F0F0F0"
        };
        if (arguments[0] && typeof arguments[0] === "object") {
            this.options = utilities.extendDefaults(defaults, arguments[0]);
        }
        finish.call(this);
    };
}());